// src/components/DogCard.js
import React, { useState } from 'react';
import AdoptionForm from './AdoptionForm';

const DogCard = ({ dogs, onAdopted }) => {
  const [showForm, setShowForm] = useState(false);

  const toggleForm = () => {
    setShowForm(!showForm);
  };

  return (
    <div className="dog-card">
      <img src={dogs.img} alt={dogs.nombre} />
      <h3>{dogs.nombre}</h3>
      <p>Edad: {dogs.edad} años</p>
      <p>Raza: {dogs.raza}</p>
      <p>Tamaño: {dogs.tamaño}</p>
      <p>Vacunado: {dogs.vacunado ? 'Sí' : 'No'}</p>
      <p>Desparasitado: {dogs.desparasitado ? 'Sí' : 'No'}</p>
      <p>Descripción: {dogs.descripcion}</p>
      <button onClick={toggleForm}>Adoptar</button>
      {showForm && <AdoptionForm dog={dog} onAdopted={onAdopted} />}
    </div>
  );
};


export default DogCard;
